"use strict";(self.webpackChunkgms_2025_scioly=self.webpackChunkgms_2025_scioly||[]).push([[967],{967:e=>{e.exports=JSON.parse('{"test_name":"Microbe Mission Test","questions":[{"question":"Which microbe is responsible for causing tuberculosis?","type":"multiple_choice","options":["A. Escherichia coli","B. Mycobacterium tuberculosis","C. Streptococcus pneumoniae","D. Staphylococcus aureus"],"answer":"B","reference":"https://www.cdc.gov/tb/default.htm"},{"question":"True or False: Viruses can reproduce on their own without a host cell.","type":"true_false","answer":"False","reference":"https://www.ncbi.nlm.nih.gov/pmc/articles/PMC7147897/"},{"question":"What is the primary function of a bacterial capsule?","type":"short_answer","answer":"Protection against the host immune system","reference":"https://www.ncbi.nlm.nih.gov/pmc/articles/PMC3246842/"},{"question":"Identify the type of microbe that causes malaria.","type":"identify_microbe","answer":"Protozoa","reference":"https://www.cdc.gov/malaria/about/index.html"},{"question":"Which of the following is a gram-positive bacterium?","type":"multiple_choice","options":["A. Escherichia coli","B. Staphylococcus aureus","C. Pseudomonas aeruginosa","D. Salmonella enterica"],"answer":"B","reference":"https://www.ncbi.nlm.nih.gov/pmc/articles/PMC4281813/"},{"question":"True or False: Fungi can be both unicellular and multicellular.","type":"true_false","answer":"True","reference":"https://www.ncbi.nlm.nih.gov/pmc/articles/PMC5852955/"},{"question":"Which type of microscope is most suitable for observing live bacterial motility?","type":"multiple_choice","options":["A. Scanning Electron Microscope (SEM)","B. Transmission Electron Microscope (TEM)","C. Phase Contrast Microscope","D. Fluorescence Microscope"],"answer":"C","reference":"https://microbenotes.com/phase-contrast-microscope/"},{"question":"What is the main purpose of Koch\'s postulates?","type":"short_answer","answer":"To establish a causal relationship between a microbe and a disease","reference":"https://www.nature.com/articles/1311581"},{"question":"Identify the microbe type for HIV.","type":"identify_microbe","answer":"Virus","reference":"https://www.cdc.gov/hiv/basics/whatishiv.html"},{"question":"Which bacterial structure is responsible for antibiotic resistance?","type":"multiple_choice","options":["A. Plasmid","B. Cell Wall","C. Ribosome","D. Flagella"],"answer":"A","reference":"https://www.ncbi.nlm.nih.gov/pmc/articles/PMC4947394/"},{"question":"True or False: Prions contain DNA or RNA.","type":"true_false","answer":"False","reference":"https://www.cdc.gov/prions/"},{"question":"What is the role of cyanobacteria in the environment?","type":"short_answer","answer":"Oxygen production and nitrogen fixation","reference":"https://www.sciencedirect.com/topics/earth-and-planetary-sciences/cyanobacteria"},{"question":"Identify the microbe type for athlete\u2019s foot.","type":"identify_microbe","answer":"Fungus","reference":"https://www.cdc.gov/fungal/diseases/ringworm/index.html"},{"question":"Which of the following microbes is an obligate intracellular parasite?","type":"multiple_choice","options":["A. Bacteria","B. Protozoa","C. Virus","D. Fungus"],"answer":"C","reference":"https://www.ncbi.nlm.nih.gov/pmc/articles/PMC3109474/"},{"question":"Identify the microscope used in this image.","type":"image_question","image":"https://upload.wikimedia.org/wikipedia/commons/3/3c/Phase_contrast_microscope.jpg","answer":"Phase Contrast Microscope","reference":"https://microbenotes.com/phase-contrast-microscope/"},{"question":"What is the bacterial structure labeled in this image?","type":"image_question","image":"https://upload.wikimedia.org/wikipedia/commons/e/eb/Gram_negative_cell_wall-en.svg","answer":"Gram-negative bacterial cell wall","reference":"https://www.ncbi.nlm.nih.gov/pmc/articles/PMC4281813/"}]}')}}]);